-- ============================================ -- FIX: Adicionar coluna EMAIL em PROFILES -- ============================================ -- Execute este SQL para corrigir o erro: -- "column profiles.email does not exist" -- ============================================
-- 1. Adicionar coluna email na tabela profiles ALTER TABLE profiles ADD COLUMN IF NOT EXISTS email TEXT;
-- 2. Preencher emails existentes dos usuários já cadastrados UPDATE profiles p SET email = u.email FROM auth.users u WHERE p.id = u.id AND p.email IS NULL;
-- 3. Criar função para preencher email automaticamente ao criar perfil CREATE OR REPLACE FUNCTION public.handle_new_user() RETURNS TRIGGER AS $$ BEGIN INSERT INTO public.profiles (id, email, full_name, username, created_at) VALUES ( NEW.id, NEW.email, COALESCE(NEW.raw_user_meta_data->>'full_name', ''), COALESCE(NEW.raw_user_meta_data->>'username', ''), NOW() ) ON CONFLICT (id) DO UPDATE SET email = NEW.email; RETURN NEW; END; $$ LANGUAGE plpgsql SECURITY DEFINER;
-- 4. Criar trigger para executar a função ao criar novo usuário DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
CREATE TRIGGER on_auth_user_created AFTER INSERT ON auth.users FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();
-- 5. Verificar se funcionou SELECT id, email, full_name FROM profiles LIMIT 5;
-- ============================================ -- ✅ Pronto! Coluna email foi adicionada. -- Agora o painel de admin deve funcionar. -- ============================================

-- ============================================ -- ⚙️ OPCIONAL: Sistema de Roles (Admin) -- ============================================ -- ⚠️⚠️⚠️ ATENÇÃO ⚠️⚠️⚠️ -- -- NÃO execute este arquivo ANTES de executar: supabase_verification_setup.sql -- -- ORDEM CORRETA: -- 1. ✅ Execute primeiro: supabase_verification_setup.sql -- 2. ✅ Crie o bucket: verification_docs -- 3. ✅ Então execute ESTE arquivo (se quiser admins) -- -- Se você receber erro "relation user_verifications does not exist", -- significa que você pulou o passo 1! -- -- Mais detalhes: veja arquivo ORDEM_EXECUCAO_SQL.md -- ============================================
-- Execute este SQL APENAS se você quiser ter admins -- que possam aprovar/rejeitar verificações manualmente
-- 1. Adicionar coluna role na tabela profiles ALTER TABLE profiles ADD COLUMN IF NOT EXISTS role TEXT DEFAULT 'user';
-- Valores possíveis: 'user', 'admin'
-- 2. Atualizar um usuário específico para ser admin -- SUBSTITUA 'seu-user-id-aqui' pelo ID do usuário que será admin UPDATE profiles SET role = 'admin' WHERE id = 'seu-user-id-aqui';
-- 3. Criar política para admins atualizarem verificações DROP POLICY IF EXISTS "Admins can update verifications" ON user_verifications;
CREATE POLICY "Admins can update verifications" ON user_verifications FOR UPDATE USING ( auth.uid() IN (SELECT id FROM profiles WHERE role = 'admin') );
-- 4. Criar política para admins visualizarem todos os documentos DROP POLICY IF EXISTS "Admins can view all verification docs" ON storage.objects;
CREATE POLICY "Admins can view all verification docs" ON storage.objects FOR SELECT USING ( bucket_id = 'verification_docs' AND auth.uid() IN (SELECT id FROM profiles WHERE role = 'admin') );
-- 5. Criar função helper para verificar se usuário é admin CREATE OR REPLACE FUNCTION is_admin() RETURNS BOOLEAN AS $$ BEGIN RETURN EXISTS ( SELECT 1 FROM profiles WHERE id = auth.uid() AND role = 'admin' ); END; $$ LANGUAGE plpgsql SECURITY DEFINER;
-- Como tornar um usuário admin: -- 1. Faça login no Supabase Dashboard -- 2. Vá em Database > Tables > profiles -- 3. Encontre o usuário -- 4. Edite e mude role de 'user' para 'admin'
-- Ou execute SQL: -- UPDATE profiles SET role = 'admin' WHERE email = 'seu-email@example.com';

-- ============================================ -- FIX: Limpar Notificações ao Aprovar/Rejeitar -- ============================================ -- Execute este SQL para que notificações sejam removidas -- automaticamente quando a verificação é aprovada/rejeitada -- ============================================
-- 1. Função para remover notificação quando verificação é atualizada CREATE OR REPLACE FUNCTION cleanup_verification_notification() RETURNS TRIGGER AS $$ BEGIN -- Se status mudou para approved ou rejected, marca notificação como lida IF (NEW.status IN ('approved', 'rejected') AND OLD.status = 'pending') THEN UPDATE admin_notifications SET read = true WHERE verification_id = NEW.id; END IF;
RETURN NEW; END; $$ LANGUAGE plpgsql SECURITY DEFINER;
-- 2. Criar trigger para limpar notificações DROP TRIGGER IF EXISTS cleanup_notification_on_verification_update ON user_verifications;
CREATE TRIGGER cleanup_notification_on_verification_update AFTER UPDATE ON user_verifications FOR EACH ROW EXECUTE FUNCTION cleanup_verification_notification();
-- 3. Limpar notificações antigas manualmente (opcional) -- Marca como lida todas as notificações de verificações já processadas UPDATE admin_notifications SET read = true WHERE verification_id IN ( SELECT id FROM user_verifications WHERE status IN ('approved', 'rejected') );
-- 4. Verificar se funcionou SELECT n.id, n.read, v.status FROM admin_notifications n LEFT JOIN user_verifications v ON n.verification_id = v.id ORDER BY n.created_at DESC LIMIT 10;
-- ============================================ -- ✅ Notificações agora são limpas automaticamente! -- ============================================

-- ============================================ -- FIX: Corrigir Foreign Key Relationship -- ============================================ -- Execute este SQL se você receber o erro: -- "Could not find a relationship between user_verifications and profiles" -- -- Este erro ocorre porque a foreign key não foi criada corretamente -- ============================================
-- 1. Verificar se a tabela user_verifications existe SELECT table_name FROM information_schema.tables WHERE table_schema = 'public' AND table_name = 'user_verifications';
-- Se retornar vazio, você precisa executar primeiro: supabase_verification_setup.sql
-- 2. Verificar foreign keys existentes SELECT tc.constraint_name, tc.table_name, kcu.column_name, ccu.table_name AS foreign_table_name, ccu.column_name AS foreign_column_name FROM information_schema.table_constraints AS tc JOIN information_schema.key_column_usage AS kcu ON tc.constraint_name = kcu.constraint_name JOIN information_schema.constraint_column_usage AS ccu ON ccu.constraint_name = tc.constraint_name WHERE tc.constraint_type = 'FOREIGN KEY' AND tc.table_name = 'user_verifications';
-- 3. Remover constraint antiga se existir (pode dar erro se não existir, tudo bem) ALTER TABLE user_verifications DROP CONSTRAINT IF EXISTS user_verifications_user_id_fkey;
-- 4. Adicionar a foreign key correta ALTER TABLE user_verifications ADD CONSTRAINT user_verifications_user_id_fkey FOREIGN KEY (user_id) REFERENCES auth.users(id) ON DELETE CASCADE;
-- 5. Criar índice para melhor performance CREATE INDEX IF NOT EXISTS idx_user_verifications_user_id ON user_verifications(user_id);
-- 6. Verificar se foi criado corretamente SELECT tc.constraint_name, tc.table_name, kcu.column_name, ccu.table_name AS foreign_table_name, ccu.column_name AS foreign_column_name FROM information_schema.table_constraints AS tc JOIN information_schema.key_column_usage AS kcu ON tc.constraint_name = kcu.constraint_name JOIN information_schema.constraint_column_usage AS ccu ON ccu.constraint_name = tc.constraint_name WHERE tc.constraint_type = 'FOREIGN KEY' AND tc.table_name = 'user_verifications';
-- Deve retornar: user_verifications_user_id_fkey
-- ============================================ -- ✅ Pronto! A foreign key foi criada. -- Agora o painel de admin deve funcionar. -- ============================================

-- ============================================ -- FIX: Políticas RLS para Admin Aprovar -- ============================================ -- Execute este SQL para corrigir o erro: -- "new row violates row-level security policy" -- -- Isso permite que você aprove/rejeite verificações -- ============================================
-- 1. Remover política antiga restritiva DROP POLICY IF EXISTS "Users can update own pending verification" ON user_verifications;
-- 2. Criar nova política que permite: -- - Usuário atualizar sua própria verificação (se pending) -- - QUALQUER usuário autenticado pode atualizar (temporário para admin)
CREATE POLICY "Authenticated users can update verifications" ON user_verifications FOR UPDATE USING (auth.uid() IS NOT NULL) WITH CHECK (auth.uid() IS NOT NULL);
-- NOTA: Esta política permite qualquer usuário autenticado atualizar. -- Se você quiser restringir apenas para admins específicos, -- execute depois: supabase_admin_roles_optional.sql
-- 3. Verificar se a política foi criada SELECT schemaname, tablename, policyname, permissive, roles, cmd, qual, with_check FROM pg_policies WHERE tablename = 'user_verifications' AND policyname = 'Authenticated users can update verifications';
-- ============================================ -- ✅ Pronto! Agora você pode aprovar/rejeitar -- ============================================
-- TESTE: -- 1. Volte ao app -- 2. Menu ☰ → Admin - Verificaciones -- 3. Clique em "Aprobar" -- 4. Deve funcionar! ✅

-- ============================================ -- SISTEMA DE NOTIFICAÇÕES PARA ADMIN -- ============================================ -- Execute este SQL para criar o sistema de notificações -- ============================================
-- 1. Criar tabela de notificações CREATE TABLE IF NOT EXISTS admin_notifications ( id UUID DEFAULT uuid_generate_v4() PRIMARY KEY, type TEXT NOT NULL, -- 'new_verification', 'verification_approved', 'verification_rejected' title TEXT NOT NULL, message TEXT NOT NULL, user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE, verification_id UUID REFERENCES user_verifications(id) ON DELETE CASCADE, read BOOLEAN DEFAULT false, created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW() );
-- 2. Criar índices CREATE INDEX IF NOT EXISTS idx_admin_notifications_read ON admin_notifications(read); CREATE INDEX IF NOT EXISTS idx_admin_notifications_created_at ON admin_notifications(created_at DESC);
-- 3. Habilitar RLS ALTER TABLE admin_notifications ENABLE ROW LEVEL SECURITY;
-- 4. Política: Qualquer usuário autenticado pode ver notificações CREATE POLICY "Authenticated users can view notifications" ON admin_notifications FOR SELECT USING (auth.uid() IS NOT NULL);
-- 5. Política: Sistema pode inserir notificações CREATE POLICY "System can insert notifications" ON admin_notifications FOR INSERT WITH CHECK (true);
-- 6. Política: Usuários podem marcar como lida CREATE POLICY "Users can update read status" ON admin_notifications FOR UPDATE USING (auth.uid() IS NOT NULL);
-- 7. Função para criar notificação quando nova verificação é enviada CREATE OR REPLACE FUNCTION notify_new_verification() RETURNS TRIGGER AS $$ BEGIN INSERT INTO admin_notifications ( type, title, message, user_id, verification_id ) SELECT 'new_verification', ' Nueva verificación recibida', 'Un usuario ha enviado documentos para verificación', NEW.user_id, NEW.id;
RETURN NEW; END; $$ LANGUAGE plpgsql SECURITY DEFINER;
-- 8. Criar trigger para nova verificação DROP TRIGGER IF EXISTS on_new_verification ON user_verifications;
CREATE TRIGGER on_new_verification AFTER INSERT ON user_verifications FOR EACH ROW EXECUTE FUNCTION notify_new_verification();
-- 9. Verificar se funcionou SELECT COUNT(*) as total_notifications FROM admin_notifications;
-- ============================================ -- ✅ Sistema de notificações criado! -- ============================================

-- Adicionar coluna verification_status na tabela profiles (se não existir)
ALTER TABLE profiles
ADD COLUMN IF NOT EXISTS verification_status TEXT DEFAULT 'not_submitted';

-- Valores possíveis: 'not_submitted', 'pending', 'approved', 'rejected'

-- Criar tabela para armazenar as verificações de documentos
CREATE TABLE IF NOT EXISTS user_verifications (
    id UUID DEFAULT uuid_generate_v4() PRIMARY KEY,
    user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
    document_type TEXT NOT NULL, -- 'dni', 'passport', 'driver_license'
    document_number TEXT NOT NULL,
    document_photo TEXT NOT NULL, -- path no storage
    selfie_photo TEXT NOT NULL, -- path no storage
    status TEXT DEFAULT 'pending' NOT NULL, -- 'pending', 'approved', 'rejected'
    submitted_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    reviewed_at TIMESTAMP WITH TIME ZONE,
    reviewed_by UUID REFERENCES auth.users(id),
    rejection_reason TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Índices para melhor performance
CREATE INDEX IF NOT EXISTS idx_user_verifications_user_id ON user_verifications(user_id);
CREATE INDEX IF NOT EXISTS idx_user_verifications_status ON user_verifications(status);

-- RLS (Row Level Security) para user_verifications
ALTER TABLE user_verifications ENABLE ROW LEVEL SECURITY;

-- Política: Usuário pode ver apenas suas próprias verificações
CREATE POLICY "Users can view own verifications"
    ON user_verifications FOR SELECT
    USING (auth.uid() = user_id);

-- Política: Usuário pode inserir sua própria verificação
CREATE POLICY "Users can insert own verification"
    ON user_verifications FOR INSERT
    WITH CHECK (auth.uid() = user_id);

-- Política: Apenas admins podem atualizar verificações (você pode ajustar isso depois)
CREATE POLICY "Admins can update verifications"
    ON user_verifications FOR UPDATE
    USING (auth.uid() IN (
        SELECT id FROM profiles WHERE role = 'admin'
    ));

-- Criar bucket de storage para documentos de verificação (se não existir)
INSERT INTO storage.buckets (id, name, public)
VALUES ('verification_docs', 'verification_docs', false)
ON CONFLICT (id) DO NOTHING;

-- Política de storage: Usuário pode fazer upload de seus próprios documentos
CREATE POLICY "Users can upload own verification docs"
    ON storage.objects FOR INSERT
    WITH CHECK (
        bucket_id = 'verification_docs' AND
        auth.uid()::text = (storage.foldername(name))[1]
    );

-- Política de storage: Usuário pode visualizar seus próprios documentos
CREATE POLICY "Users can view own verification docs"
    ON storage.objects FOR SELECT
    USING (
        bucket_id = 'verification_docs' AND
        auth.uid()::text = (storage.foldername(name))[1]
    );

-- Política de storage: Admins podem visualizar todos os documentos
CREATE POLICY "Admins can view all verification docs"
    ON storage.objects FOR SELECT
    USING (
        bucket_id = 'verification_docs' AND
        auth.uid() IN (SELECT id FROM profiles WHERE role = 'admin')
    );

-- Trigger para atualizar updated_at automaticamente
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER update_user_verifications_updated_at
    BEFORE UPDATE ON user_verifications
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

-- Comentários para documentação
COMMENT ON TABLE user_verifications IS 'Armazena as verificações de identidade dos usuários';
COMMENT ON COLUMN user_verifications.status IS 'Status da verificação: pending, approved, rejected';
COMMENT ON COLUMN profiles.verification_status IS 'Status da última verificação do usuário';

