-- Adicionar coluna verification_status na tabela profiles (se não existir) ALTER TABLE profiles ADD COLUMN IF NOT EXISTS verification_status TEXT DEFAULT 'not_submitted';
-- Valores possíveis: 'not_submitted', 'pending', 'approved', 'rejected'
-- Criar tabela para armazenar as verificações de documentos CREATE TABLE IF NOT EXISTS user_verifications ( id UUID DEFAULT uuid_generate_v4() PRIMARY KEY, user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL, document_type TEXT NOT NULL, -- 'dni', 'passport', 'driver_license' document_number TEXT NOT NULL, document_photo TEXT NOT NULL, -- path no storage selfie_photo TEXT NOT NULL, -- path no storage status TEXT DEFAULT 'pending' NOT NULL, -- 'pending', 'approved', 'rejected' submitted_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(), reviewed_at TIMESTAMP WITH TIME ZONE, reviewed_by UUID REFERENCES auth.users(id), rejection_reason TEXT, created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(), updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW() );
-- Índices para melhor performance CREATE INDEX IF NOT EXISTS idx_user_verifications_user_id ON user_verifications(user_id); CREATE INDEX IF NOT EXISTS idx_user_verifications_status ON user_verifications(status);
-- RLS (Row Level Security) para user_verifications ALTER TABLE user_verifications ENABLE ROW LEVEL SECURITY;
-- Política: Usuário pode ver apenas suas próprias verificações CREATE POLICY "Users can view own verifications" ON user_verifications FOR SELECT USING (auth.uid() = user_id);
-- Política: Usuário pode inserir sua própria verificação CREATE POLICY "Users can insert own verification" ON user_verifications FOR INSERT WITH CHECK (auth.uid() = user_id);
-- Política: Usuário pode atualizar sua própria verificação (apenas se status = pending) CREATE POLICY "Users can update own pending verification" ON user_verifications FOR UPDATE USING (auth.uid() = user_id AND status = 'pending');
-- NOTA: Para permitir que admins atualizem verificações, adicione a coluna 'role' -- na tabela profiles e depois execute: -- CREATE POLICY "Admins can update verifications" -- ON user_verifications FOR UPDATE -- USING (auth.uid() IN (SELECT id FROM profiles WHERE role = 'admin'));
-- Criar bucket de storage para documentos de verificação (se não existir) INSERT INTO storage.buckets (id, name, public) VALUES ('verification_docs', 'verification_docs', false) ON CONFLICT (id) DO NOTHING;
-- Política de storage: Usuário pode fazer upload de seus próprios documentos CREATE POLICY "Users can upload own verification docs" ON storage.objects FOR INSERT WITH CHECK ( bucket_id = 'verification_docs' AND auth.uid()::text = (storage.foldername(name))[1] );
-- Política de storage: Usuário pode visualizar seus próprios documentos CREATE POLICY "Users can view own verification docs" ON storage.objects FOR SELECT USING ( bucket_id = 'verification_docs' AND auth.uid()::text = (storage.foldername(name))[1] );
-- NOTA: Para permitir que admins visualizem todos os documentos, adicione a coluna 'role' -- na tabela profiles e depois execute: -- CREATE POLICY "Admins can view all verification docs" -- ON storage.objects FOR SELECT -- USING ( -- bucket_id = 'verification_docs' AND -- auth.uid() IN (SELECT id FROM profiles WHERE role = 'admin') -- );
-- Trigger para atualizar updated_at automaticamente CREATE OR REPLACE FUNCTION update_updated_at_column() RETURNS TRIGGER AS $$ BEGIN NEW.updated_at = NOW(); RETURN NEW; END; $$ LANGUAGE plpgsql;
CREATE TRIGGER update_user_verifications_updated_at BEFORE UPDATE ON user_verifications FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();
-- Comentários para documentação COMMENT ON TABLE user_verifications IS 'Armazena as verificações de identidade dos usuários'; COMMENT ON COLUMN user_verifications.status IS 'Status da verificação: pending, approved, rejected'; COMMENT ON COLUMN profiles.verification_status IS 'Status da última verificação do usuário';

-- ============================================ -- SCRIPT COMPLETO - ADICIONAR NOVAS COLUNAS -- Execute este script no SQL Editor do Supabase -- ============================================ -- Este script adiciona as colunas delivery_type e photos -- ============================================
-- 1. ADICIONAR COLUNA delivery_type -- ============================================ ALTER TABLE public.items ADD COLUMN IF NOT EXISTS delivery_type TEXT DEFAULT 'pickup';
-- Adicionar constraint para garantir valores válidos ALTER TABLE public.items DROP CONSTRAINT IF EXISTS check_delivery_type;
ALTER TABLE public.items ADD CONSTRAINT check_delivery_type CHECK (delivery_type IN ('pickup', 'delivery', 'both'));
-- Atualizar items existentes que não têm delivery_type UPDATE public.items SET delivery_type = 'pickup' WHERE delivery_type IS NULL;
-- ============================================ -- 2. ADICIONAR COLUNA photos -- ============================================ -- Adicionar coluna photos (JSONB array para até 3 fotos) ALTER TABLE public.items ADD COLUMN IF NOT EXISTS photos JSONB DEFAULT '[]'::jsonb;
-- Migrar dados existentes: se houver photo_url, adicionar ao array photos UPDATE public.items SET photos = jsonb_build_array(photo_url) WHERE photo_url IS NOT NULL AND (photos IS NULL OR photos = '[]'::jsonb);
-- ============================================ -- 3. CRIAR ÍNDICES PARA PERFORMANCE -- ============================================ CREATE INDEX IF NOT EXISTS idx_items_delivery_type ON public.items(delivery_type); CREATE INDEX IF NOT EXISTS idx_items_photos ON public.items USING GIN (photos);
-- ============================================ -- 4. VERIFICAÇÃO -- ============================================ -- Verificar se as colunas foram criadas SELECT column_name, data_type, column_default FROM information_schema.columns WHERE table_name = 'items' AND column_name IN ('delivery_type', 'photos') ORDER BY column_name;
-- Ver exemplo dos dados migrados SELECT id, title, delivery_type, photo_url, photos FROM public.items LIMIT 5;
-- ============================================ -- FIM DO SCRIPT -- ============================================ -- ✅ Colunas adicionadas: -- - delivery_type (TEXT) - tipo de entrega -- - photos (JSONB) - array de até 3 fotos -- ✅ Dados migrados automaticamente -- ✅ Índices criados para performance -- ============================================
