option.value:
import React, { useState, useEffect } from 'react';
import { View, ActivityIndicator, AppRegistry } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { supabase } from './supabase';

import AuthScreen from './src/screens/AuthScreen';
import HomeScreen from './src/screens/HomeScreen'; // Nova tela Home
import MainMarketplace from './src/screens/MainMarketplace'; // Tela que acabamos de criar
import AddItemScreen from './src/screens/AddItemScreen'; // Tela de cadastro de item
import EditItemScreen from './src/screens/EditItemScreen'; // Tela de edição de item
import ItemDetailsScreen from './src/screens/ItemDetailsScreen'; // Tela de detalhes do item
import RequestRentalScreen from './src/screens/RequestRentalScreen'; // Tela de solicitação de aluguel
import ProfileScreen from './src/screens/ProfileScreen'; // Tela de perfil do usuário

const Stack = createNativeStackNavigator();

export default function App() {
    const [session, setSession] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Lógica para checar a sessão (mantida do seu código anterior)
        supabase.auth.getSession().then(({ data: { session } }) => {
            setSession(session);
            setLoading(false);
        });

        const { data: authListener } = supabase.auth.onAuthStateChange(
            (_event, session) => {
                setSession(session);
                setLoading(false);
            }
        );

        return () => authListener.subscription.unsubscribe();
    }, []);

    if (loading) {
        return (
            <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                <ActivityIndicator size="large" />
            </View>
        );
    }

    // Se estiver logado, exibe o Stack Navigator com as telas do app
    if (session && session.user) {
        return (
            <NavigationContainer>
                <Stack.Navigator
                    screenOptions={{
                        headerShown: false,
                    }}
                >
                    <Stack.Screen name="HomeScreen">
                        {(props) => <HomeScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="Home">
                        {(props) => <MainMarketplace {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="AddItem">
                        {(props) => <AddItemScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="EditItem">
                        {(props) => <EditItemScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="ItemDetails">
                        {(props) => <ItemDetailsScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="RequestRental">
                        {(props) => <RequestRentalScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="Profile">
                        {(props) => <ProfileScreen {...props} session={session} />}
                    </Stack.Screen>
                </Stack.Navigator>
            </NavigationContainer>
        );
    }

    // Se não estiver logado, exibe apenas a tela de autenticação
    return <AuthScreen />;
}

// Registrar o componente para Expo
AppRegistry.registerComponent('main', () => App);

import React, { useState, useEffect } from 'react';
import { View, ActivityIndicator, AppRegistry } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { supabase } from './supabase';

import AuthScreen from './src/screens/AuthScreen';
import HomeScreen from './src/screens/HomeScreen';
import MainMarketplace from './src/screens/MainMarketplace';
import TipsBeforeAddingScreen from './src/screens/TipsBeforeAddingScreen';
import AddItemFormScreen from './src/screens/AddItemFormScreen';
import EditItemScreen from './src/screens/EditItemScreen';
import ItemDetailsScreen from './src/screens/ItemDetailsScreen';
import RequestRentalScreen from './src/screens/RequestRentalScreen';
import ProfileScreen from './src/screens/ProfileScreen';

const Stack = createNativeStackNavigator();

export default function App() {
    const [session, setSession] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Lógica para checar a sessão (mantida do seu código anterior)
        supabase.auth.getSession().then(({ data: { session } }) => {
            setSession(session);
            setLoading(false);
        });

        const { data: authListener } = supabase.auth.onAuthStateChange(
            (_event, session) => {
                setSession(session);
                setLoading(false);
            }
        );

        return () => authListener.subscription.unsubscribe();
    }, []);

    if (loading) {
        return (
            <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                <ActivityIndicator size="large" />
            </View>
        );
    }

    // Se estiver logado, exibe o Stack Navigator com as telas do app
    if (session && session.user) {
        return (
            <NavigationContainer>
                <Stack.Navigator
                    screenOptions={{
                        headerShown: false,
                    }}
                >
                    <Stack.Screen name="HomeScreen">
                        {(props) => <HomeScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="Home">
                        {(props) => <MainMarketplace {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="AddItem">
                        {(props) => <TipsBeforeAddingScreen {...props} />}
                    </Stack.Screen>

                    <Stack.Screen name="AddItemForm">
                        {(props) => <AddItemFormScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="EditItem">
                        {(props) => <EditItemScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="ItemDetails">
                        {(props) => <ItemDetailsScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="RequestRental">
                        {(props) => <RequestRentalScreen {...props} session={session} />}
                    </Stack.Screen>

                    <Stack.Screen name="Profile">
                        {(props) => <ProfileScreen {...props} session={session} />}
                    </Stack.Screen>
                </Stack.Navigator>
            </NavigationContainer>
        );
    }

    // Se não estiver logado, exibe apenas a tela de autenticação
    return <AuthScreen />;
}

// Registrar o componente para Expo
AppRegistry.registerComponent('main', () => App);

-- ============================================ -- SCRIPT PARA ADICIONAR COLUNA delivery_type -- Execute este script no SQL Editor do Supabase -- ============================================
-- Adicionar coluna delivery_type à tabela items ALTER TABLE public.items ADD COLUMN IF NOT EXISTS delivery_type TEXT DEFAULT 'pickup';
-- Adicionar constraint para garantir valores válidos ALTER TABLE public.items ADD CONSTRAINT check_delivery_type CHECK (delivery_type IN ('pickup', 'delivery', 'both'));
-- Atualizar items existentes que não têm delivery_type UPDATE public.items SET delivery_type = 'pickup' WHERE delivery_type IS NULL;
-- Verificar se a coluna foi criada SELECT column_name, data_type, column_default FROM information_schema.columns WHERE table_name = 'items' AND column_name = 'delivery_type';

-- ============================================ -- SCRIPT PARA ADICIONAR COLUNA photos -- Execute este script no SQL Editor do Supabase -- ============================================
-- Adicionar coluna photos à tabela items (tipo JSONB para armazenar array de strings) ALTER TABLE public.items ADD COLUMN IF NOT EXISTS photos JSONB DEFAULT '[]'::jsonb;
-- Migrar dados existentes: se houver photo_url, adicionar ao array photos UPDATE public.items SET photos = jsonb_build_array(photo_url) WHERE photo_url IS NOT NULL AND (photos IS NULL OR photos = '[]'::jsonb);
-- Criar índice para melhor performance em queries de fotos CREATE INDEX IF NOT EXISTS idx_items_photos ON public.items USING GIN (photos);
-- Verificar se a coluna foi criada SELECT column_name, data_type, column_default FROM information_schema.columns WHERE table_name = 'items' AND column_name = 'photos';
-- Ver exemplo dos dados migrados SELECT id, title, photo_url, photos FROM public.items LIMIT 5;

import React, { useState } from 'react';
import { StyleSheet, View, Text, TextInput, ScrollView, Alert, TouchableOpacity, Image, ActivityIndicator, Platform, StatusBar } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Picker } from '@react-native-picker/picker';
import * as ImagePicker from 'expo-image-picker';
import { LinearGradient } from 'expo-linear-gradient';
import { supabase } from '../../supabase';
import * as FileSystem from 'expo-file-system/legacy';
import { decode } from 'base64-arraybuffer';

const SUPABASE_URL = 'https://fvhnkwxvxnsatqmljnxu.supabase.co';

export default function AddItemScreen({ session, navigation }) {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [pricePerDay, setPricePerDay] = useState('');
    const [category, setCategory] = useState('Herramientas');
    const [location, setLocation] = useState('');
    const [deliveryType, setDeliveryType] = useState('pickup');
    const [loading, setLoading] = useState(false);
    const [photos, setPhotos] = useState([null, null, null]);
    const [photoPaths, setPhotoPaths] = useState([null, null, null]);

    const categories = ['Herramientas', 'Electrónicos', 'Deportes', 'Moda', 'Vehículos', 'Otros'];

    const pickImage = async (index) => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 0.5,
        });

        if (!result.canceled && result.assets && result.assets.length > 0) {
            const newPhotos = [...photos];
            newPhotos[index] = result.assets[0].uri;
            setPhotos(newPhotos);
        }
    };

    const removePhoto = (index) => {
        const newPhotos = [...photos];
        newPhotos[index] = null;
        setPhotos(newPhotos);
    };

    const uploadImage = async (uri) => {
        const user = session.user;
        if (!user) {
            Alert.alert('Error de Sesión', 'El usuario no está conectado.');
            return null;
        }

        try {
            const base64 = await FileSystem.readAsStringAsync(uri, { encoding: 'base64' });
            const filePath = `${user.id}/${Date.now()}.jpg`;

            const { data, error } = await supabase.storage
                .from('item_photos')
                .upload(filePath, decode(base64), {
                    contentType: 'image/jpeg',
                    upsert: false,
                });

            if (error) {
                Alert.alert('Error en la Carga', 'Error al subir la imagen: ' + error.message);
                return null;
            }

            return data.path;
        } catch (err) {
            Alert.alert('Error', 'Error inesperado durante la carga: ' + err.message);
            return null;
        }
    };

    async function handleSubmit() {
        const hasAtLeastOnePhoto = photos.some(photo => photo !== null);

        if (!title || !description || !pricePerDay || !location || !hasAtLeastOnePhoto) {
            Alert.alert('Completa Todos los Campos', '¡Recuerda añadir al menos una foto!');
            return;
        }

        setLoading(true);
        const uploadedPaths = [];

        for (let i = 0; i < photos.length; i++) {
            if (photos[i]) {
                const uploadedPath = await uploadImage(photos[i]);
                if (uploadedPath) {
                    uploadedPaths.push(uploadedPath);
                }
            }
        }

        if (uploadedPaths.length === 0) {
            Alert.alert('Error', 'No se pudo subir ninguna foto');
            setLoading(false);
            return;
        }

        const { error } = await supabase
            .from('items')
            .insert({
                owner_id: session.user.id,
                title,
                description,
                price_per_day: parseFloat(pricePerDay),
                category,
                location,
                photo_url: uploadedPaths[0],
                photos: uploadedPaths,
                delivery_type: deliveryType,
            });

        setLoading(false);

        if (error) {
            Alert.alert('Error al Anunciar', error.message);
        } else {
            Alert.alert('¡Éxito!', '¡Tu artículo ha sido anunciado en el marketplace!');
            setTitle('');
            setDescription('');
            setPricePerDay('');
            setLocation('');
            setPhotos([null, null, null]);
            setPhotoPaths([null, null, null]);
        }
    }

    return (
        <SafeAreaView style={styles.safeContainer}>
            <StatusBar barStyle="dark-content" backgroundColor="#F8F9FA" />

            {/* Header Moderno */}
            <View style={styles.headerContainer}>
                <TouchableOpacity
                    style={styles.backButton}
                    onPress={() => navigation.goBack()}
                    activeOpacity={0.7}
                >
                    <Text style={styles.backArrow}>←</Text>
                </TouchableOpacity>
                <View style={styles.headerTitleContainer}>
                    <Text style={styles.headerTitle}>Anunciar Artículo</Text>
                    <Text style={styles.headerSubtitle}>Comparte lo que no usas</Text>
                </View>
                <View style={styles.headerSpacer} />
            </View>

            <ScrollView style={styles.scrollContent} showsVerticalScrollIndicator={false}>
                {/* Card: Información Básica */}
                <View style={styles.card}>
                    <Text style={styles.cardTitle}> Información Básica</Text>

                    <Text style={styles.label}>Título del Anuncio</Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={setTitle}
                        value={title}
                        placeholder="Ej: Taladro Bosch 18V"
                        placeholderTextColor="#999"
                        maxLength={80}
                    />

                    <Text style={styles.label}>Descripción Completa</Text>
                    <TextInput
                        style={[styles.input, styles.multilineInput]}
                        onChangeText={setDescription}
                        value={description}
                        placeholder="Describe el estado, accesorios incluidos y condiciones de alquiler..."
                        placeholderTextColor="#999"
                        multiline
                        numberOfLines={4}
                    />

                    <Text style={styles.label}>Categoría</Text>
                    <View style={styles.pickerContainer}>
                        <Picker
                            selectedValue={category}
                            onValueChange={(itemValue) => setCategory(itemValue)}
                        >
                            {categories.map((cat, index) => (
                                <Picker.Item key={index} label={cat} value={cat} />
                            ))}
                        </Picker>
                    </View>
                </View>

                {/* Card: Precio y Ubicación */}
                <View style={styles.card}>
                    <Text style={styles.cardTitle}> Precio y Ubicación</Text>

                    <Text style={styles.label}>Precio por Día</Text>
                    <View style={styles.priceInputContainer}>
                        <Text style={styles.currencySymbol}>€</Text>
                        <TextInput
                            style={styles.priceInput}
                            onChangeText={setPricePerDay}
                            value={pricePerDay}
                            placeholder="0.00"
                            placeholderTextColor="#999"
                            keyboardType="numeric"
                        />
                        <Text style={styles.perDay}>/día</Text>
                    </View>

                    <Text style={styles.label}>Ubicación</Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={setLocation}
                        value={location}
                        placeholder="Ej: Madrid - Centro"
                        placeholderTextColor="#999"
                    />
                </View>

                {/* Card: Tipo de Entrega */}
                <View style={styles.card}>
                    <Text style={styles.cardTitle}> Tipo de Entrega</Text>
                    <View style={styles.deliveryTypeContainer}>
                        <TouchableOpacity
                            style={[styles.deliveryOption, deliveryType === 'pickup' && styles.deliveryOptionActive]}
                            onPress={() => setDeliveryType('pickup')}
                            activeOpacity={0.7}
                        >
                            <Text style={styles.deliveryOptionIcon}></Text>
                            <Text style={[styles.deliveryOptionText, deliveryType === 'pickup' && styles.deliveryOptionTextActive]}>
                                Retira en Lugar
                            </Text>
                            {deliveryType === 'pickup' && (
                                <View style={styles.deliveryCheckmark}>
                                    <Text style={styles.deliveryCheckmarkText}>✓</Text>
                                </View>
                            )}
                        </TouchableOpacity>

                        <TouchableOpacity
                            style={[styles.deliveryOption, deliveryType === 'delivery' && styles.deliveryOptionActive]}
                            onPress={() => setDeliveryType('delivery')}
                            activeOpacity={0.7}
                        >
                            <Text style={styles.deliveryOptionIcon}></Text>
                            <Text style={[styles.deliveryOptionText, deliveryType === 'delivery' && styles.deliveryOptionTextActive]}>
                                Entrego en Casa
                            </Text>
                            {deliveryType === 'delivery' && (
                                <View style={styles.deliveryCheckmark}>
                                    <Text style={styles.deliveryCheckmarkText}>✓</Text>
                                </View>
                            )}
                        </TouchableOpacity>

                        <TouchableOpacity
                            style={[styles.deliveryOption, deliveryType === 'both' && styles.deliveryOptionActive]}
                            onPress={() => setDeliveryType('both')}
                            activeOpacity={0.7}
                        >
                            <Text style={styles.deliveryOptionIcon}></Text>
                            <Text style={[styles.deliveryOptionText, deliveryType === 'both' && styles.deliveryOptionTextActive]}>
                                Ambas Opciones
                            </Text>
                            {deliveryType === 'both' && (
                                <View style={styles.deliveryCheckmark}>
                                    <Text style={styles.deliveryCheckmarkText}>✓</Text>
                                </View>
                            )}
                        </TouchableOpacity>
                    </View>
                </View>

                {/* Card: Fotos */}
                <View style={styles.card}>
                    <Text style={styles.cardTitle}> Fotos del Artículo</Text>
                    <Text style={styles.cardSubtitle}>Sube hasta 3 fotos - La primera será la principal</Text>

                    <View style={styles.photosGrid}>
                        {photos.map((photo, index) => (
                            <View key={index} style={styles.photoContainer}>
                                <TouchableOpacity
                                    onPress={() => pickImage(index)}
                                    style={[styles.photoPlaceholder, index === 0 && styles.photoPlaceholderPrimary]}
                                >
                                    {photo ? (
                                        <>
                                            <Image source={{ uri: photo }} style={styles.previewImage} />
                                            <TouchableOpacity style={styles.removePhotoButton} onPress={() => removePhoto(index)}>
                                                <Text style={styles.removePhotoText}>✕</Text>
                                            </TouchableOpacity>
                                            {index === 0 && (
                                                <View style={styles.primaryBadge}>
                                                    <Text style={styles.primaryBadgeText}>Principal</Text>
                                                </View>
                                            )}
                                        </>
                                    ) : (
                                        <View style={styles.addPhotoContent}>
                                            <Text style={styles.addPhotoIcon}></Text>
                                            <Text style={styles.addPhotoText}>
                                                {index === 0 ? 'Foto Principal' : `Foto ${index + 1}`}
                                            </Text>
                                        </View>
                                    )}
                                </TouchableOpacity>
                            </View>
                        ))}
                    </View>
                </View>

                {/* Botão Publicar */}
                <TouchableOpacity
                    style={[styles.publishButton, loading && styles.publishButtonDisabled]}
                    onPress={handleSubmit}
                    disabled={loading}
                    activeOpacity={0.8}
                >
                    <LinearGradient
                        colors={loading ? ['#95a5a6', '#7f8c8d'] : ['#FF6B35', '#F7931E']}
                        start={{ x: 0, y: 0 }}
                        end={{ x: 1, y: 0 }}
                        style={styles.publishButtonGradient}
                    >
                        {loading ? (
                            <View style={styles.publishButtonContent}>
                                <ActivityIndicator color="#fff" size="small" />
                                <Text style={styles.publishButtonText}>Procesando...</Text>
                            </View>
                        ) : (
                            <View style={styles.publishButtonContent}>
                                <Text style={styles.publishButtonIcon}></Text>
                                <Text style={styles.publishButtonText}>Anunciar Artículo</Text>
                            </View>
                        )}
                    </LinearGradient>
                </TouchableOpacity>

                <View style={{height: 30}} />
            </ScrollView>
        </SafeAreaView>
    );
}

